let activeUser = null;
let loggedUser = null;

const generateQualification = () => {
    return (Math.random() * (5 - 3) + 3).toFixed(1);
}

const manageProfileView = () => {
    switch (activeUser.userType) {
        case 'DM':
            itemsPS.forEach(element => {
                element.style.display = "none";
            });
            break;
        case 'PS':
            const renderServices = (services) => {
                services.forEach(element => {
                    console.log(element);
                    const service = document.createElement('li');
                    service.setAttribute('class', 'profile-items__item profile-item-service');
                    service.innerHTML = `
                    <div class="profile-item__body-left">
                    <p class="profile-item__header">${element.name}</p>
                    <p class="profile-item__description">${element.description}</p>
                    </div>
                    <div class="profile-item__body-right">
                    
                    <button class="profile-item__button primary-button js-request-service-btn">Solicitar Servicio</button>
                    
                    <p class="profile-item__schedule">De ${element.startTime} a ${element.endTime}</p>
                    <p class="profile-item__price">₡${element.price}</p>
                    </div>
                    `;
                    service.setAttribute('style', 'display: flex');
                    servicesContainer.appendChild(service);
                });
            }

            const updateCurrentUser = async() => {
                fetch(`http://localhost:8080/services/get-user-services/${activeUser._id}`)
                    .then(response => response.json())
                    .then(data => renderServices(data));
            }

            updateCurrentUser();
            break;
    }
}

const renderProfile = async() => {
    profileName.innerHTML = activeUser.name;
    profilePhoto.setAttribute('src', activeUser.imageURL);
    switch (activeUser.userType) {
        case 'DM':
            profileAge.innerHTML = calculateAge(activeUser.birthdate);
            profileTypeUser.innerHTML = 'Dueño de mascota';
            break;
        case 'PS':

            profileBusiness.innerHTML = activeUser.nameCompany;
            profileServiceType.innerHTML = activeUser.serviceType;
            profileTypeUser.innerHTML = 'Proveedor de servicio';
            break;
    }
    profileLocation.innerHTML = `${await getProvinceName(activeUser.province)}, ${await getCantName(activeUser.province, activeUser.cant)}`;
    profileEmail.innerHTML = activeUser.email;
    profileNumber.innerHTML = activeUser.phoneNumber;
    profileQualification.innerHTML = generateQualification();
}

const renderUserItems = () => {
    if (activeUser.userType == "DM") {
        itemPet.style.display = "flex";
        profileItemsTitle.innerHTML = 'Mascotas';
    } else if (activeUser.userType == "PS") {
        profileItemsTitle.innerHTML = 'Servicios';
        switch (activeUser.serviceType) {
            case 'walk':
                itemServiceWalk.style.display = "flex";
                break;
            case 'grooming':
                itemServiceGrooming.style.display = "flex";
                break;
            case 'take-care':
                itemServiceTakeCare.style.display = "flex";
                break;
        }
    }
}

const manageRoles = () => {
    if (loggedUser.userType == "Admin") {
        profileAdminOptions.forEach(element => {
            element.style.display = "block";
        });
    } else {
        profileOptions.forEach(element => {
            element.style.display = "block";
        });

        if (loggedUser.userType == "DM") {
            requestService.forEach(btn => {
                btn.style.display = "block";
            });
        }
    }
}

const capitalize = (text) => {
    if (text) {
        const resultado = text.replace(/^\w/, (c) => c.toUpperCase());
        return resultado;
    }
}

const createSideLinks = (titulo, currentData) => {

    let fragmentLinks = document.createDocumentFragment();

    const resultado = currentData.find(section => {
        return section.name == `${titulo}`;
    });

    const valor = currentData.indexOf(resultado);

    const keys = Object.values(currentData[valor]);

    const linksCount = keys.length;

    let count = 0;
    for (let index = 0; index <= linksCount; index++) {
        let aLink = document.createElement('a');
        aLink.classList.add('side-link');
        const texto = capitalize(currentData[valor].links[index]);
        if (texto) {
            aLink.textContent = texto;
            fragmentLinks.appendChild(aLink);
        }
    }

    return fragmentLinks;
}

const loadSideSections = (currentData) => {
    const fragment = document.createDocumentFragment();
    const sections = currentData.length;

    for (let index = 0; index < sections; index++) {
        let sideSection = document.createElement('DIV');
        sideSection.classList.add('side-section');
        let sideTitle = document.createElement('DIV');
        sideTitle.classList.add('side-title');
        let propertyName = currentData[index].name[0].toString();
        let titleCapitalized = capitalize(propertyName);
        sideTitle.textContent = titleCapitalized;
        let sideLinkContainer = document.createElement('DIV');
        sideLinkContainer.classList.add('side-link-container');
        let linksToAppend = createSideLinks(propertyName, currentData);
        sideLinkContainer.appendChild(linksToAppend);
        sideSection.appendChild(sideTitle);
        sideSection.appendChild(sideLinkContainer);
        fragment.appendChild(sideSection);
    }
    sideContainer.appendChild(fragment);
}

const insertSideTargets = (titulo = 'entrar', enlace = '#', svg) => {
    let selected;
    const links = document.querySelectorAll('.side-link');
    const capitalizado = capitalize(titulo);

    links.forEach(link => {

        if (link.textContent == `${capitalizado}`) {
            selected = link;

            link.setAttribute('href', `${enlace}`);

            link.innerHTML = `${svg} ${capitalizado}`;
        }
    });
}

